using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program {\n    static void Main() {\n        // Example log data in memory\n        var logs = new[] {\n            new { Date = DateTime.Now, Message = "Test failed" },\n            new { Date = DateTime.Now, Message = "Test passed" }\n        };\n\n        // Creating XML document\n        XDocument xmlDoc = new XDocument(\n            new XElement("Logs",\n                from log in logs\n                select new XElement("Log",\n                    new XElement("Date", log.Date),\n                    new XElement("Message", log.Message)\n                )\n            ));\n\n        xmlDoc.Save("report.xml");\n\n        // Creating XHTML document\n        XDocument htmlDoc = new XDocument(\n            new XElement("html", new XAttribute(XNamespace.Xmlns + "xhtml", "http://www.w3.org/1999/xhtml"),\n                new XElement("head",\n                    new XElement("title", "Test Report")\n                ),\n                new XElement("body",\n                    new XElement("h1", "Log Entries"),\n                    new XElement("ul",\n                        from log in logs\n                        select new XElement("li",\n                            new XElement("span", new XAttribute("class", "date"), log.Date.ToString()),\n                            new XElement("span", new XAttribute("class", "message"), log.Message)\n                        )\n                    )\n                )\n            )\n        );\n\n        htmlDoc.Save("report.xhtml");\n    }\n}