// Declare a delegate for updating the textbox\nprivate delegate void UpdateTextBoxDelegate(string value);\n\n// Method to safely update the textbox from any thread\nprivate void SetTextSafe(string text)\n{\n    if (this.txtPrice.InvokeRequired) // Check if we need to invoke to avoid cross-thread operation errors\n    {\n        // Use BeginInvoke to execute the delegate on the UI thread without blocking\n        this.BeginInvoke(new UpdateTextBoxDelegate(SetTextSafe), new object[] { text });\n    }\n    else\n    {\n        // We are already on the UI thread, safe to update directly\n        this.txtPrice.Text = text;\n    }\n}\n\n// Event handler for price changes in the financial library\nprivate void OnPriceChanged(string price)\n{\n    // Throttle updates (optional): only update every X milliseconds if needed\n    SetTextSafe(price);\n}