using System.Collections;\nusing System.Collections.ObjectModel;\nusing System.ComponentModel;\n\npublic class MyViewModel : INotifyPropertyChanged\n{\n    private ObservableCollection<string> _myArray;\n    public ObservableCollection<string> MyArray\n    {\n        get { return _myArray; }\n        set \n        {\n            if (_myArray != value)\n            {\n                _myArray = value;\n                OnPropertyChanged(nameof(MyArray));\n                OnPropertyChanged(nameof(IsArrayEmpty)); // Notify that IsArrayEmpty changed\n            }\n        }\n    }\n\n    public bool IsArrayEmpty => MyArray.Count == 0;\n\n    public event PropertyChangedEventHandler PropertyChanged;\n\n    protected virtual void OnPropertyChanged(string propertyName)\n    {\n        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyName));\n    }\n}