using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        string groupName = "Administrators";\n        DirectoryEntry dirEnt = new DirectoryEntry();\n\n        using (DirectorySearcher srch = new DirectorySearcher(dirEnt))\n        {\n            srch.PageSize = 1000;\n            srch.Filter = $"(&(objectClass=group)(cn={groupName}))";\n            \n            SearchResult result = srch.FindOne();\n            if (result != null)\n            {\n                PropertyCollection properties = result.Properties;\n                if (properties.Contains("member"))\n                {\n                    foreach (string member in properties["member"])\n                    {\n                        Console.WriteLine(member);\n                    }\n                }\n                else\n                {\n                    Console.WriteLine($"No members found for group: {groupName}");\n                }\n            }\n            else\n            {\n                Console.WriteLine($"Group not found: {groupName}");\n            }\n        }\n    }\n}