public class MyUserControl : System.Web.UI.UserControl\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            InitializeMultiSelect();\n        }\n    }\n\n    private void InitializeMultiSelect()\n    {\n        // Generate a unique ID for each instance of the UserControl\n        string uniqueId = this.ClientID;\n        string scriptKey = $"uniqueScript_{uniqueId}";\n\n        // Create the JavaScript code to be registered\n        string script = $@"\n            function initializeMultiSelectFor{uniqueId}() {{\n                $(document).ready(function() {{\n                    $('#{uniqueId} select').multiselect();\n                }});\n            }}\n\n            // Execute this function when page loads or UpdatePanel updates\n            if (typeof Sys !== 'undefined' && typeof Sys.WebForms !== 'undefined') {{\n                Sys.WebForms.PageRequestManager.getInstance().add_pageLoaded(initializeMultiSelectFor{uniqueId});\n            }} else {{\n                $(document).readyinitializeMultiSelectFor{uniqueId}());\n            }}\n        ";\n\n        // Register the script with ScriptManager\n        ScriptManager.RegisterStartupScript(this, GetType(), scriptKey, script, true);\n    }\n}