using System;\nusing System.ServiceModel;\n\nnamespace SecureWcfService\n{\n    [ServiceContract]\n    public interface ISecureService\n    {\n        [OperationContract]\n        string GetData(string value);\n    }\n\n    public class SecureService : ISecureService\n    {\n        public string GetData(string value)\n        {\n            return $"You entered: {value}";\n        }\n    }\n\n    class Program\n    {\n        static void Main()\n        {\n            var host = new ServiceHost(typeof(SecureService));\n            \n            // Configure WSHttpBinding with Message security\n            var binding = new WSHttpBinding();\n            binding.Security.Mode = SecurityMode.Message;\n\n            // Add endpoint to the host\n            host.AddServiceEndpoint(typeof(ISecureService), binding, "http://localhost:8000/SecureWcf");\n\n            Console.WriteLine("Starting WCF Service...");\n            host.Open();\n\n            Console.ReadLine();\n            host.Close();\n        }\n    }\n}