using System;\nusing KasperskyLab.NSApi;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an instance of KAVScanner\n        var scanner = (IObjectFactory)new KavScanner();\n\n        // Set the type of scanner object\n        IAVPPlugin plugin = (IAVPPlugin)scanner.CreateInstance("{425E0DBD-1789-4B3A-BCF3-E969EEF1E48C}");\n\n        // Initialize the scanner\n        plugin.Initialize(iavpConstants.avcInitNormal);\n\n        // Define path to file you want to scan\n        string filePath = @"C:\path\to\your\file.exe";\n\n        // Perform the scan and get the result\n        IResultsSet results = (IResultsSet)plugin.ScanFile(filePath, iavpConstants.avcScanNoMask);\n        \n        // Process the results\n        while (results.Next())\n        {\n            Console.WriteLine("Infected: " + results.getString(iavpConstants.avrInfected));\n        }\n\n        // Uninitialize the scanner\n        plugin.Uninitialize();\n    }\n}