using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    static async Task Main(string[] args)\n    {\n        try\n        {\n            // URL to get data from\n            string url = "https://example.com";\n            \n            // Fetch the page content\n            var htmlContent = await GetHtmlAsync(url);\n            \n            // Parse the content with HtmlAgilityPack\n            var document = new HtmlDocument();\n            document.LoadHtml(htmlContent);\n            \n            // Example of how to select nodes, e.g., all links on the page\n            HtmlNodeCollection nodes = document.DocumentNode.SelectNodes("//a[@href]");\n            \n            foreach (HtmlNode node in nodes)\n            {\n                Console.WriteLine(node.GetAttributeValue("href", string.Empty));\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n\n    private static async Task<string> GetHtmlAsync(string url)\n    {\n        var response = await client.GetAsync(url);\n        response.EnsureSuccessStatusCode();\n        return await response.Content.ReadAsStringAsync();\n    }\n}