using System;\nusing System.Collections.Generic;\n\npublic class MyType\n{\n    public int Id { get; set; }\n    // Add other properties as necessary\n\n    public override bool Equals(object obj)\n    {\n        if (obj is MyType other)\n            return this.Id == other.Id;\n        return false;\n    }\n\n    public override int GetHashCode()\n    {\n        return Id.GetHashCode();\n    }\n}\n\npublic class CustomList : List<MyType>\n{\n    // Shadow the Contains method\n    public new bool Contains(MyType item)\n    {\n        // Implement your custom logic here\n        foreach (var element in this)\n        {\n            if (element.Id == item.Id)\n            {\n                return true;\n            }\n        }\n        return false;\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        CustomList myList = new CustomList();\n        myList.Add(new MyType { Id = 1 });\n\n        bool contains = myList.Contains(new MyType { Id = 1 });\n        Console.WriteLine(contains); // Outputs: True\n    }\n}