[ServiceBehavior]\npublic class MyService : IMyService\n{\n    public MyType GetMyTypes(string criteria)\n    {\n        try\n        {\n            return GetMyTypes();\n        }\n        catch (Exception ex)\n        {\n            WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n            var errorDetail = new\n            {\n                error = new\n                {\n                    code = 403,\n                    message = "A Big fat error occurred",\n                    details = ex.Message\n                }\n            };\n            throw new WebFaultException<string>(JsonConvert.SerializeObject(errorDetail),\n                                              HttpStatusCode.Forbidden);\n        }\n    }\n}