using System;\nusing System.Net;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        CookieContainer cookieJar = new CookieContainer();\n        HttpClientHandler handler = new HttpClientHandler() { CookieContainer = cookieJar };\n\n        using (HttpClient client = new HttpClient(handler))\n        {\n            // First request to establish a session and save cookies\n            HttpResponseMessage response = await client.GetAsync("https://example.com/login");\n            Console.WriteLine(response.StatusCode);\n\n            // Second request with the same session/cookies\n            response = await client.PostAsync("https://example.com/data", new FormUrlEncodedContent(new[]\n            {\n                new KeyValuePair<string, string>("key", "value")\n            }));\n            response.EnsureSuccessStatusCode();\n            string responseBody = await response.Content.ReadAsStringAsync();\n            Console.WriteLine(responseBody);\n        }\n    }\n}