using System;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    private WebBrowser webBrowser;\n\n    public MainForm()\n    {\n        InitializeComponent();\n\n        webBrowser = new WebBrowser();\n        webBrowser.Dock = DockStyle.Fill;\n        this.Controls.Add(webBrowser);\n\n        webBrowser.DocumentCompleted += (s, e) => CheckFramesReady(e.Url);\n        webBrowser.Navigate("http://example.com");\n    }\n\n    private void CheckFramesReady(Uri frameUrl)\n    {\n        if (webBrowser.IsBusy) return;\n\n        var jsCheckScript = @"\n            (function() { \n                var framesDone = [];\n                for(var i = 0; i < window.frames.length; i++) {\n                    try {\n                        framesDone.push(window.frames[i].document.readyState === 'complete');\n                    } catch(e) {\n                        framesDone.push(false);\n                    }\n                }\n                if (framesDone.every(function(r){ return r; })) {\n                    true; // All frames are loaded\n                } else { \n                    false; \n                }\n            })();\n        ";\n\n        dynamic domDoc = webBrowser.Document.DomDocument;\n        bool allFramesReady = domDoc.parentWindow.execScript(jsCheckScript, "javascript").Pop();\n\n        if (allFramesReady)\n        {\n            MessageBox.Show("All frames are loaded!");\n            // Perform any action you need after all frames have completed loading.\n        }\n    }\n}