private static EventWaitHandle _shutdownEvent = new ManualResetEvent(false);\n\n   // In your main form's Closing event handler\n   private void MainForm_Closing(object sender, FormClosingEventArgs e)\n   {\n       _shutdownEvent.Set();  // Signal non-UI threads to stop\n   }\n\n   // Example of a background thread that listens for the shutdown signal\n   private void WorkerThreadMethod()\n   {\n       while (!_shutdownEvent.WaitOne(0)) // Check if we should exit\n       {\n           // Do work here...\n       }\n   }