using System;\n\npublic class Model\n{\n    public string Data { get; set; }\n}\n\npublic class View\n{\n    private readonly Model _model;\n\n    public View(Model model)\n    {\n        _model = model;\n    }\n\n    public void Display()\n    {\n        Console.WriteLine("Current Data: " + _model.Data);\n    }\n\n    public string GetUserInput()\n    {\n        return Console.ReadLine();\n    }\n}\n\npublic class Controller\n{\n    private readonly Model _model;\n    private readonly View _view;\n\n    public Controller(Model model, View view)\n    {\n        _model = model;\n        _view = view;\n    }\n\n    public void UpdateModel(string newData)\n    {\n        _model.Data = newData;\n    }\n\n    public void HandleInput()\n    {\n        string input = _view.GetUserInput();\n        if (!string.IsNullOrEmpty(input))\n        {\n            UpdateModel(input);\n        }\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var model = new Model { Data = "Initial data" };\n        var view = new View(model);\n        var controller = new Controller(model, view);\n\n        while (true)\n        {\n            view.Display();\n            Console.Write("Enter new data (or type 'exit' to quit): ");\n            if (_view.GetUserInput().ToLower() == "exit")\n                break;\n            \n            controller.HandleInput();\n        }\n    }\n}