using System;\n\npublic class DateRecurrenceChecker\n{\n    public static bool IsDateInRangeOfRecurrence(\n        DateTime startDate, \n        DateTime endDate, \n        TimeSpan interval,\n        int numberOfRecurrences,\n        int recurrenceUnit,\n        DateTime checkDate)\n    {\n        for (int i = 0; i < numberOfRecurrences; i++)\n        {\n            // Add the recurrence unit to get the start date of the current recurrence\n            DateTime currentStartDate = startDate.Add(new TimeSpan(0, 0, 0, 0, \n                interval.Days * 24 * 60 * 60 * 1000 * i * recurrenceUnit));\n            \n            // Add the duration of the span to get the end date\n            DateTime currentEndDate = currentStartDate + (endDate - startDate);\n            \n            if (checkDate >= currentStartDate && checkDate <= currentEndDate)\n                return true;\n        }\n        \n        return false;\n    }\n\n    public static void Main()\n    {\n        DateTime startDate = new DateTime(2009, 01, 01, 1, 0, 0);\n        DateTime endDate = new DateTime(2009, 01, 06, 17, 0, 0);\n        TimeSpan interval = new TimeSpan(365, 0, 0, 0); // yearly recurrence\n        int numberOfRecurrences = 5; // example: check for 5 years\n        int recurrenceUnit = 1; \n\n        DateTime checkDate = new DateTime(2010, 01, 03, 10, 0, 0);\n        \n        bool result = IsDateInRangeOfRecurrence(startDate, endDate, interval, \n            numberOfRecurrences, recurrenceUnit, checkDate);\n\n        Console.WriteLine(result); // Output: True or False depending on the date\n    }\n}