using System.IO;\nusing System.Web.UI;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void CreatePdf(HttpResponse Response)\n{\n    Document pdfDoc = new Document(PageSize.A4, 10f, 10f, 100f, 0f);\n    PdfWriter.GetInstance(pdfDoc, Response.OutputStream);\n\n    pdfDoc.Open();\n\n    // Add content to the document\n    pdfDoc.Add(new Paragraph("Professional Report Title", FontFactory.GetFont(FontFactory.HELVETICA_BOLD, 16)));\n    pdfDoc.Add(new Paragraph("This is a professionally formatted report."));\n\n    pdfDoc.Close();\n\n    Response.ContentType = "application/pdf";\n    Response.AddHeader("content-disposition", "attachment;filename=Report.pdf");\n    Response.Cache.SetCacheability(HttpCacheability.NoCache);\n    Response.Write(pdfDoc);\n\n    Response.End();\n}