public class HomeController : Controller\n{\n    public ActionResult GetRegionsByCountry(int countryId)\n    {\n        // Assume you have a method to fetch regions from the database\n        var regions = GetRegionsFromDatabase(countryId);\n        \n        return Json(regions, JsonRequestBehavior.AllowGet);\n    }\n\n    private IEnumerable<SelectListItem> GetRegionsFromDatabase(int countryId)\n    {\n        // Example data source\n        var regions = new List<string>\n        {\n            "Region1", "Region2", "Region3"\n        };\n\n        return regions.Select(r => new SelectListItem { Text = r, Value = r });\n    }\n}