public ActionResult Index()\n{\n    var model = new ViewModel\n    {\n        Countries = GetCountriesFromDatabase(),\n        // Other initializations...\n    };\n\n    return View(model);\n}\n\nprivate IEnumerable<SelectListItem> GetCountriesFromDatabase()\n{\n    // Example data source\n    var countries = new List<string>\n    {\n        "Country1", "Country2", "Country3"\n    };\n\n    return countries.Select(c => new SelectListItem { Text = c, Value = (countries.IndexOf(c) + 1).ToString() });\n}