using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmail()\n{\n    SmtpClient client = new SmtpClient("smtp.yourdomain.com")\n    {\n        Port = 25, // Or the port number configured in Exchange\n        Credentials = new NetworkCredential("username@yourdomain.com", "password"),\n        EnableSsl = false, // Change to true if using SSL/TLS\n        Timeout = 10000\n    };\n\n    MailMessage mailMessage = new MailMessage\n    {\n        From = new MailAddress("from@yourdomain.com"),\n        Subject = "Test Email",\n        Body = "This is a test email body."\n    };\n    mailMessage.To.Add(new MailAddress("recipient@externaldomain.com"));\n\n    client.Send(mailMessage);\n}