public class Program\n{\n    public static void Main()\n    {\n        FixedLengthRecord record = new FixedLengthRecord\n        {\n            FirstName = "John",\n            LastName = "Doe",\n            Address = "1234 Elm St",\n            City = "Springfield",\n            State = "IL",\n            Zip = "62701"\n        };\n\n        string fixedLengthString = record.ToString();\n        Console.WriteLine("Fixed Length String: " + fixedLengthString);\n\n        FixedLengthRecord newRecord = new FixedLengthRecord();\n        newRecord.FromFixedLengthString(fixedLengthString);\n        Console.WriteLine($"New Record: {newRecord.FirstName} {newRecord.LastName}, {newRecord.Address}, {newRecord.City}, {newRecord.State} {newRecord.Zip}");\n    }\n}