using System;\nusing System.Text;\n\npublic class FixedLengthRecord\n{\n    private const int FirstNameLength = 10;\n    private const int LastNameLength = 10;\n    private const int AddressLength = 25;\n    private const int CityLength = 20;\n    private const int StateLength = 2;\n    private const int ZipLength = 5;\n\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public string Address { get; set; }\n    public string City { get; set; }\n    public string State { get; set; }\n    public string Zip { get; set; }\n\n    // Ensure each field is of the specified length\n    private static string NormalizeString(string value, int length)\n    {\n        byte[] bytes = Encoding.ASCII.GetBytes(value);\n        if (bytes.Length > length)\n            return Encoding.ASCII.GetString(bytes, 0, length);\n        else\n            return value.PadRight(length, ' ');\n    }\n\n    // Convert the object to a fixed-length string suitable for URL parameters\n    public override string ToString()\n    {\n        StringBuilder sb = new StringBuilder();\n        sb.Append(NormalizeString(FirstName, FirstNameLength));\n        sb.Append(NormalizeString(LastName, LastNameLength));\n        sb.Append(NormalizeString(Address, AddressLength));\n        sb.Append(NormalizeString(City, CityLength));\n        sb.Append(NormalizeString(State, StateLength));\n        sb.Append(NormalizeString(Zip, ZipLength));\n\n        return sb.ToString();\n    }\n\n    // Populate the object from a fixed-length string\n    public void FromFixedLengthString(string data)\n    {\n        FirstName = data.Substring(0, FirstNameLength).TrimEnd(' ');\n        LastName = data.Substring(FirstNameLength, LastNameLength).TrimEnd(' ');\n        Address = data.Substring(FirstNameLength + LastNameLength, AddressLength).TrimEnd(' ');\n        City = data.Substring(FirstNameLength + LastNameLength + AddressLength, CityLength).TrimEnd(' ');\n        State = data.Substring(FirstNameLength + LastNameLength + AddressLength + CityLength, StateLength).TrimEnd(' ');\n        Zip = data.Substring(FirstNameLength + LastNameLength + AddressLength + CityLength + StateLength, ZipLength).TrimEnd(' ');\n    }\n}