// Assuming `latestRunDate` is fetched from SQL Server query.\n    DataTable accessData = GetAccessData(latestRunDate);\n\n    private DataTable GetAccessData(DateTime latestRunDate)\n    {\n        string connectionString = "YourAccessConnectionStringHere";\n        using (OleDbConnection conn = new OleDbConnection(connectionString))\n        {\n            conn.Open();\n            // Use parameterized queries to prevent SQL Injection.\n            string query = "SELECT * FROM ProductionRuns WHERE RunDate > @LatestRunDate";\n            using (OleDbCommand cmd = new OleDbCommand(query, conn))\n            {\n                cmd.Parameters.AddWithValue("@LatestRunDate", latestRunDate);\n                using (OleDbDataAdapter adapter = new OleDbDataAdapter(cmd))\n                {\n                    DataTable dataTable = new DataTable();\n                    adapter.Fill(dataTable);\n                    return dataTable;\n                }\n            }\n        }\n    }