protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming SystemGrid_Sorting fills a DataTable with your data\n        DataTable dataTable = GetDataFromDatabase();\n        \n        // Create a HyperLinkField for the ID column\n        HyperLinkField idHyperlink = new HyperLinkField();\n        idHyperlink.HeaderText = "ID";\n        idHyperlink.DataNavigateUrlFields = new string[] { "ObjectID" }; // Name of your ID field in the DataTable\n        idHyperlink.DataNavigateUrlFormatString = "~/DetailPage.aspx?object_id={0}"; // URL pattern with a query string parameter\n        idHyperlink.DataPropertyName = "ObjectID"; // Assuming "ObjectID" is the column name for the IDs\n\n        // Add the HyperLinkField to the GridView's columns collection\n        SystemGrid.Columns.Add(idHyperlink);\n\n        // Bind your data to the GridView\n        SystemGrid.DataSource = dataTable;\n        SystemGrid.DataBind();\n    }\n}\n\n// Example method to fetch data from Oracle DB, you should replace it with your actual code\nprivate DataTable GetDataFromDatabase()\n{\n    // Your existing SystemGrid_Sorting logic here\n    // This is just a placeholder for returning a filled DataTable\n    DataTable dataTable = new DataTable();\n    dataTable.Columns.Add("ObjectID", typeof(int));\n    dataTable.Rows.Add(1);\n    dataTable.Rows.Add(2);\n    return dataTable;\n}