// TCP Server\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass TCPServer {\n    static void Main() {\n        TcpListener listener = new TcpListener(IPAddress.Any, 13000);\n        listener.Start();\n        Console.WriteLine("server started");\n        TcpClient client = listener.AcceptTcpClient();\n        NetworkStream ns = client.GetStream();\n        byte[] buffer = Encoding.ASCII.GetBytes("Hello Client!");\n        ns.Write(buffer, 0, buffer.Length);\n\n        ns.Close();\n        client.Close();\n        listener.Stop();\n    }\n}\n\n// TCP Client\nusing System.Net.Sockets;\nusing System.Text;\n\nclass TCPClient {\n    static void Main() {\n        TcpClient client = new TcpClient("localhost", 13000);\n        NetworkStream ns = client.GetStream();\n\n        byte[] buffer = new byte[256];\n        int bytes = ns.Read(buffer, 0, buffer.Length);\n        string receivedData = Encoding.ASCII.GetString(buffer, 0, bytes);\n        Console.WriteLine(receivedData);\n\n        ns.Close();\n        client.Close();\n    }\n}