using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string encryptedBase64 = "YourEncryptedMessageFromPerl"; // Replace with your Perl output\n        byte[] key = Encoding.ASCII.GetBytes("0123456789abcdef01234567");\n        byte[] iv  = Encoding.ASCII.GetBytes("abcdefghijhklmno");\n\n        using (AesCryptoServiceProvider aesAlg = new AesCryptoServiceProvider())\n        {\n            aesAlg.Key = key;\n            aesAlg.IV = iv;\n\n            ICryptoTransform decryptor = aesAlg.CreateDecryptor(aesAlg.Key, aesAlg.IV);\n\n            using (MemoryStream msDecrypt = new MemoryStream(Convert.FromBase64String(encryptedBase64)))\n            {\n                using (CryptoStream csDecrypt = new CryptoStream(msDecrypt, decryptor, CryptoStreamMode.Read))\n                {\n                    using (StreamReader srDecrypt = new StreamReader(csDecrypt))\n                    {\n                        string decryptedMessage = srDecrypt.ReadToEnd();\n                        Console.WriteLine("Decrypted message: " + decryptedMessage);\n                    }\n                }\n            }\n        }\n    }\n}