private string DoValidation(string columnName)\n{\n    string error = null;\n    switch (columnName)\n    {\n        case "Age":\n            if (this.Age < 0 || this.Age > 120)\n                error = "Age must be between 0 and 120.";\n            break;\n        // Add more cases as necessary for other properties\n    }\n    hasErrors = !string.IsNullOrEmpty(error);\n    return error;\n}