using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // This assumes you have passed or configured SolutionPath somewhere accessible at runtime.\n        string solutionDirectory = AppDomain.CurrentDomain.GetData("SolutionPath") as string;\n\n        if (string.IsNullOrEmpty(solutionDirectory))\n        {\n            Console.WriteLine("Solution directory not set.");\n        }\n        else\n        {\n            Console.WriteLine($"Solution Directory: {solutionDirectory}");\n        }\n\n        // As an alternative, you can read from a file or use other configuration methods to get the path.\n    }\n}