private static readonly MemoryCache _cache = MemoryCache.Default;\n\nprotected void Application_BeginRequest(Object sender, EventArgs e)\n{\n    if (IsCategoryUrl())\n    {\n        string categoryName = ParseCategoryNameFromUrl(Request.Url);\n        \n        // Try to retrieve cached category ID\n        var categoryId = _cache.Get(categoryName) as int?;\n        if (!categoryId.HasValue)\n        {\n            // If not in cache, fetch from database and add to cache\n            categoryId = GetCategoryIdByNameFromDB(categoryName);\n            \n            // Define a policy for the cache item\n            CacheItemPolicy policy = new CacheItemPolicy();\n            policy.AbsoluteExpiration = DateTimeOffset.Now.AddMinutes(10); // Set expiration\n\n            _cache.Set(categoryName, categoryId.Value, policy);\n        }\n        \n        Context.RewritePath("/Category.aspx?id=" + categoryId.Value);\n    }\n}