using System;\nusing System.IO.Pipes;\nusing System.Text;\nusing System.Threading.Tasks;\n\nnamespace AddInCommunication\n{\n    public class NamedPipeServer\n    {\n        private readonly string _pipeName = "MyNamedPipe";\n\n        public async Task StartAsync()\n        {\n            using (var pipeServer = new NamedPipeServerStream(_pipeName, PipeDirection.Out))\n            {\n                await pipeServer.WaitForConnectionAsync();\n                \n                var writer = new StreamWriter(pipeServer);\n                await writer.WriteLineAsync("Hello from Word!");\n                await writer.FlushAsync();\n                \n                // Close the connection when done\n                pipeServer.Close();\n            }\n        }\n    }\n}