private static bool IsTesting = true; // This can be set via an environment variable or a config setting\n\n   public static string GetConnectionString()\n   {\n       if (IsTesting)\n       {\n           return ConfigurationManager.ConnectionStrings["TestConnectionString"].ConnectionString;\n       }\n       else\n       {\n           return ConfigurationManager.ConnectionStrings["ProductionConnectionString"].ConnectionString;\n       }\n   }