using System.Security.Cryptography;\nusing System.IO;\nusing Org.BouncyCastle.Crypto.Parameters;\nusing Org.BouncyCastle.OpenSsl;\n\npublic void ExportPublicKey(string publicKeyPath)\n{\n    using (var rsa = new RSACryptoServiceProvider(2048)) // or load your existing key here\n    {\n        var dotNetParams = rsa.ExportParameters(false);\n        var bcParams = DotNetUtilities.GetRsaPublicKeyParameters(dotNetParams);\n\n        using (var stringWriter = new StringWriter())\n        {\n            var pemWriter = new PemWriter(stringWriter);\n            pemWriter.WriteObject(bcParams);\n            File.WriteAllText(publicKeyPath, stringWriter.ToString());\n        }\n    }\n}