using System;\n   using System.Diagnostics;\n\n   public class WebsiteScreenshotCapture\n   {\n       public void CaptureScreenshot(string url, string outputPath)\n       {\n           ProcessStartInfo processInfo = new ProcessStartInfo();\n           processInfo.FileName = "wkhtmltoimage"; // Ensure this path is accessible or use full path\n           processInfo.Arguments = $"--quality 100 \"{url}\" \"{outputPath}\"";\n           processInfo.CreateNoWindow = true;\n           processInfo.UseShellExecute = false;\n\n           using (Process proc = new Process())\n           {\n               proc.StartInfo = processInfo;\n               proc.Start();\n               proc.WaitForExit(5000); // Wait for the process to exit; adjust timeout as needed\n               if (proc.ExitCode != 0)\n                   throw new Exception("wkhtmltoimage failed with error code: " + proc.ExitCode);\n           }\n       }\n   }