using WeifenLuo.WinFormsUI.Docking;\n\npublic class MainForm : Form\n{\n    private DockPanel dockPanel;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        InitializeDockPanel();\n    }\n\n    private void InitializeComponent()\n    {\n        // Other initialization code for your form...\n    }\n\n    private void InitializeDockPanel()\n    {\n        dockPanel = new DockPanel();\n        this.Controls.Add(dockPanel);\n        dockPanel.Dock = DockStyle.Fill;\n\n        // Example of adding a document\n        Form contentForm = new Form();\n        contentForm.Text = "Document";\n        contentForm.TopLevel = false;\n        contentForm.FormBorderStyle = FormBorderStyle.None;\n        contentForm.Dock = DockStyle.Fill;\n\n        DockContent documentPane = new DockContent { Text = "Sample Document", Content = contentForm };\n        documentPane.Show(dockPanel, DockState.Document);\n    }\n}