using System.Diagnostics;\n\npublic void ConvertHtmlToPdf(string inputFilePath, string outputFilePath)\n{\n    var processInfo = new ProcessStartInfo("wkhtmltopdf.exe", $"{inputFilePath} {outputFilePath}")\n    {\n        CreateNoWindow = true,\n        UseShellExecute = false\n    };\n\n    using (var process = new Process { StartInfo = processInfo })\n    {\n        process.Start();\n        process.WaitForExit(60000); // Timeout after one minute\n        if (process.ExitCode != 0)\n        {\n            throw new Exception($"PDF conversion failed with exit code {process.ExitCode}");\n        }\n    }\n}