using (SqlConnection conn = new SqlConnection(connectionString))\n{\n    try\n    {\n        // Open the connection to the database.\n        conn.Open();\n\n        // Start a local transaction with read committed isolation level.\n        SqlTransaction tx = conn.BeginTransaction(IsolationLevel.ReadCommitted);\n\n        // Pass the transaction into each of the methods in the DAL layer.\n        DalMethod1(conn, tx);\n        DalMethod2(conn, tx);\n        DalMethod3(conn, tx);\n\n        // Commit the transaction.\n        tx.Commit();\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Error occurred: " + ex.Message);\n        // If an exception occurs, rollback the transaction.\n        conn.Rollback();\n    }\n}\n\nvoid DalMethod1(SqlConnection connection, SqlTransaction transaction)\n{\n    using (SqlCommand cmd = new SqlCommand("YourCommandText", connection, transaction))\n    {\n        cmd.ExecuteNonQuery();\n    }\n}\n\nvoid DalMethod2(SqlConnection connection, SqlTransaction transaction)\n{\n    using (SqlCommand cmd = new SqlCommand("AnotherCommandText", connection, transaction))\n    {\n        cmd.ExecuteNonQuery();\n    }\n}\n\nvoid DalMethod3(SqlConnection connection, SqlTransaction transaction)\n{\n    using (SqlCommand cmd = new SqlCommand("OneMoreCommandText", connection, transaction))\n    {\n        cmd.ExecuteNonQuery();\n    }\n}