public class ContentService {\n    private readonly MyDataContext _db;\n\n    public ContentService(MyDataContext db) {\n        _db = db;\n    }\n\n    public string GetRenderedContent(int contentId) {\n        var content = _db.Contents.FirstOrDefault(c => c.Id == contentId);\n        if (content == null) return string.Empty;\n\n        // Assuming a method to convert markdown or wiki markup to HTML\n        return MarkdownRenderer.ToHtml(content.MarkupText);\n    }\n}