using System;\nusing Oracle.ManagedDataAccess.Client;\nusing Oracle.ManagedDataAccess.Types;\n\nclass Program\n{\n    static void Main()\n    {\n        using (OracleConnection connection = new OracleConnection("your_connection_string"))\n        {\n            try\n            {\n                connection.Open();\n                OracleCommand command = new OracleCommand("GETCOMPANYLIST", connection);\n                command.CommandType = System.Data.CommandType.StoredProcedure;\n\n                var companiesParam = new OracleParameter("CompanyDetails", OracleDbType.Object, ParameterDirection.Output) { UdtTypeName = "COMPANY_TAB" };\n                command.Parameters.Add(companiesParam);\n\n                command.ExecuteNonQuery();\n\n                // Convert the returned UDT to a more usable form\n                OracleObject resultCollection = (OracleObject)companiesParam.Value;\n                OracleCustomTypeFactory.CreateObject(command.Connection, "COMPANY_OBJ");\n\n                // Add your code here to iterate over and access the companies and orders\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine(ex.Message);\n            }\n        }\n    }\n}