using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\n\npublic class MapScreenCapture\n{\n    public static void SaveMapViewAsImage(MapPoint mappointControl, string filePath)\n    {\n        // Determine the size of the control to capture\n        Rectangle bounds = new Rectangle(0, 0, mappointControl.Width, mappointControl.Height);\n\n        // Create a bitmap in memory with the same dimensions as the MAP Control\n        Bitmap mapBitmap = new Bitmap(bounds.Width, bounds.Height);\n        \n        // Graphics object from the bitmap in memory\n        using (Graphics g = Graphics.FromImage(mapBitmap))\n        {\n            // Copy content from the MAP control to the bitmap\n            g.CopyFromScreen(mappointControl.PointToScreen(Point.Empty), Point.Empty, bounds.Size);\n        }\n\n        // Save the file in PNG format\n        mapBitmap.Save(filePath, ImageFormat.Png);\n    }\n}