using System;\nusing System.DirectoryServices;\n\npublic class LdapSearcherExample {\n    public static void SearchInLdap(string ldapPath, string searchFilter) {\n        DirectoryEntry entry = new DirectoryEntry(ldapPath);\n        DirectorySearcher searcher = new DirectorySearcher(entry) {\n            Filter = searchFilter,\n            PageSize = 1000\n        };\n\n        foreach (SearchResult result in searcher.FindAll()) {\n            Console.WriteLine("Found: " + result.Properties["cn"][0]);\n        }\n    }\n\n    public static void Main(string[] args) {\n        string ldapPath = "LDAP://your-ldap-server";\n        string searchFilter = "(objectClass=user)";\n        SearchInLdap(ldapPath, searchFilter);\n    }\n}