using System;\nusing System.Windows.Documents;\nusing System.Windows.Markup;\n\npublic string ConvertHtmlToXaml(string html)\n{\n    var range = new TextRange(new FlowDocument().ContentStart, new FlowDocument().ContentEnd);\n    using (var stream = new System.IO.MemoryStream(System.Text.Encoding.UTF8.GetBytes(html)))\n    {\n        range.Load(stream, DataFormats.Html);\n    }\n    return XamlWriter.Save(range.Document);\n}