using Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        // Open the 32-bit view of the registry from a 64-bit application\n        using (RegistryKey key = RegistryKey.OpenBaseKey(RegistryHive.LocalMachine, RegistryView.Registry32))\n        {\n            // Navigate to your specific subkey path\n            using (RegistryKey subkey = key.OpenSubKey(@"SOFTWARE\YourCompany\YourApp"))\n            {\n                if (subkey != null)\n                {\n                    // Read the value from the registry\n                    object value = subkey.GetValue("YourValueName");\n                    // Use the value as needed\n                }\n            }\n        }\n    }\n}