using System.Web.Mvc;\n\npublic static class MenuHelper \n{\n    public static string IsSelected(this HtmlHelper html, string controller = null, string action = null) \n    {\n        var routeData = html.ViewContext.RouteData.Values;\n        string currentController = (string)(routeData["controller"] ?? string.Empty);\n        string currentAction = (string)(routeData["action"] ?? string.Empty);\n\n        if ((string.IsNullOrEmpty(controller) || controller.ToLower() == currentController.ToLower()) &&\n            (string.IsNullOrEmpty(action) || action.ToLower() == currentAction.ToLower())) \n        {\n            return "selected";\n        }\n\n        return "";\n    }\n}