using System;\nusing System.IO.MemoryMappedFiles;\n\npublic class MemoryMappedFileViewer\n{\n    private MemoryMappedFile _memoryMappedFile;\n    private MemoryMappedViewAccessor _accessor;\n\n    public MemoryMappedFileViewer(string filePath)\n    {\n        _memoryMappedFile = MemoryMappedFile.CreateFromFile(filePath, FileMode.Open, "MyMemoryMap", 0, MemoryMappedFileAccess.Read);\n        _accessor = _memoryMappedFile.CreateViewAccessor();\n    }\n\n    public byte ReadByte(long position)\n    {\n        byte value;\n        _accessor.Read(position, out value);\n        return value;\n    }\n\n    public void Dispose()\n    {\n        _accessor?.Dispose();\n        _memoryMappedFile?.Dispose();\n    }\n}