// In your Razor view (.cshtml)\n<form action="@Url.Action("SubmitEmail", "Contact")" method="post">\n    <label for="name">Name:</label>\n    <input type="text" name="Name" id="name" required/>\n    \n    <label for="email">Email:</label>\n    <input type="email" name="Email" id="email" required/>\n\n    <!-- Honeypot field -->\n    <div style="display: none;">\n        <label for="honeypot">Leave this field empty</label>\n        <input type="text" name="Honeypot" id="honeypot"/>\n    </div>\n\n    <!-- JavaScript test -->\n    <input type="hidden" name="javascript_test" value="false" id="js-test"/>\n\n    <script type="text/javascript">\n        document.getElementById('js-test').value = 'true';\n    </script>\n    \n    <button type="submit">Send</button>\n</form>\n\n// In your Controller (ContactController.cs)\n[HttpPost]\npublic ActionResult SubmitEmail(string Name, string Email, string Honeypot, string javascript_test)\n{\n    if (!string.IsNullOrEmpty(Honeypot) || javascript_test != "true")\n    {\n        // This is likely a bot submission\n        return Content("Thank you!", "text/plain");\n    }\n\n    // Process the valid form submission\n    // Send email, log data, etc.\n    \n    return RedirectToAction("Success");\n}