using System;\nusing System.Net.Sockets;\nusing System.Text.RegularExpressions;\n\nclass VentriloStatus\n{\n    static void Main()\n    {\n        string serverIp = "127.0.0.1"; // IP of the Ventrilo server\n        int port = 3784;              // Default Ventrilo Telnet port\n\n        using TcpClient client = new TcpClient(serverIp, port);\n        using NetworkStream stream = client.GetStream();\n        System.IO.StreamReader reader = new System.IO.StreamReader(stream);\n\n        byte[] command = System.Text.Encoding.ASCII.GetBytes("status\n");\n        stream.Write(command, 0, command.Length);\n\n        string output = "";\n        while ((output = reader.ReadLine()) != null)\n        {\n            if (output.Contains("Chan ID"))\n                break;\n        }\n\n        MatchCollection matches = Regex.Matches(output + reader.ReadToEnd(), @"(\d+)\s+(.+?)\s+::\s(.+)");\n\n        foreach (Match match in matches)\n        {\n            Console.WriteLine($"Channel: #{match.Groups[1]} - Name: {match.Groups[2]}");\n        }\n    }\n}