protected void Page_Load(object sender, EventArgs e)\n{\n    // Set the response content type to XML\n    Response.ContentType = "text/xml";\n\n    // Start XML output\n    Response.Write("<?xml version='1.0' encoding='utf-8'?>");\n    Response.Write("<rows>");\n    Response.Write("<page>1</page>");\n    Response.Write("<total>25</total>");\n    Response.Write("<records>3</records>");\n\n    // Sample data - in a real scenario, this would be populated dynamically\n    string[,] data = new string[,]\n    {\n        { "Row 1, Column 1", "Row 1, Column 2", "Row 1, Column 3" },\n        { "Row 2, Column 1", "Row 2, Column 2", "Row 2, Column 3" },\n        { "Row 3, Column 1", "Row 3, Column 2", "Row 3, Column 3" }\n    };\n\n    // Loop through the data and write each row\n    for (int i = 0; i < 3; i++)\n    {\n        Response.Write("<row id='" + (i + 1) + "'>");\n        for (int j = 0; j < 3; j++)\n        {\n            Response.Write("<cell>" + data[i, j] + "</cell>");\n        }\n        Response.Write("</row>");\n    }\n\n    // End XML output\n    Response.Write("</rows>");\n\n    // End the response to prevent any additional content from being sent\n    Response.End();\n}