public class VoteController : ApiController\n{\n    [HttpPost]\n    [Route("api/vote")]\n    public IHttpActionResult Post([FromBody] VoteModel model)\n    {\n        // Here you would handle the logic for updating votes, e.g., saving to database\n        // For demonstration, just return the vote count incremented by 1\n        model.VoteCount++;\n\n        return Json(model);\n    }\n}\n\npublic class VoteModel\n{\n    public int QuestionId { get; set; }\n    public int AnswerId { get; set; }\n    public int VoteCount { get; set; }\n}