using System;\nusing System.Data;\nusing System.Linq;\n\npublic class ProductFilterExample\n{\n    public static void Main()\n    {\n        // Sample data setup\n        DataSet dataSet = CreateSampleDataSet();\n\n        string supplierName = "Microsoft";\n        string featureName = "Width";\n\n        var filteredProducts = from p in dataSet.Tables["Product"].AsEnumerable()\n                               join f in dataSet.Tables["Feature"].AsEnumerable() on p.Field<int>("ProductId") equals f.Field<int>("ProductId")\n                               join s in dataSet.Tables["Supplier"].AsEnumerable() on p.Field<int>("SupplierId") equals s.Field<int>("SupplierId")\n                               where f.Field<string>("FeatureName") == featureName &&\n                                     s.Field<string>("SupplierName") == supplierName\n                               select new\n                               {\n                                   ProductName = p.Field<string>("ProductName"),\n                                   SupplierName = s.Field<string>("SupplierName"),\n                                   FeatureName = f.Field<string>("FeatureName")\n                               };\n\n        foreach (var product in filteredProducts)\n        {\n            Console.WriteLine($"Product: {product.ProductName}, Supplier: {product.SupplierName}, Feature: {product.FeatureName}");\n        }\n    }\n\n    private static DataSet CreateSampleDataSet()\n    {\n        var dataSet = new DataSet();\n\n        // Define tables\n        DataTable products = new DataTable("Product");\n        DataTable suppliers = new DataTable("Supplier");\n        DataTable features = new DataTable("Feature");\n\n        // Define columns for each table\n        products.Columns.Add(new DataColumn("ProductId", typeof(int)));\n        products.Columns.Add(new DataColumn("ProductName", typeof(string)));\n        products.Columns.Add(new DataColumn("SupplierId", typeof(int)));\n\n        suppliers.Columns.Add(new DataColumn("SupplierId", typeof(int)));\n        suppliers.Columns.Add(new DataColumn("SupplierName", typeof(string)));\n\n        features.Columns.Add(new DataColumn("FeatureId", typeof(int)));\n        features.Columns.Add(new DataColumn("ProductId", typeof(int)));\n        features.Columns.Add(new DataColumn("FeatureName", typeof(string)));\n\n        // Insert sample data into tables\n        products.Rows.Add(1, "Laptop", 1);\n        products.Rows.Add(2, "Smartphone", 1);\n\n        suppliers.Rows.Add(1, "Microsoft");\n        suppliers.Rows.Add(2, "Apple");\n\n        features.Rows.Add(1, 1, "Width");\n        features.Rows.Add(2, 1, "Height");\n        features.Rows.Add(3, 2, "Weight");\n\n        // Add tables to DataSet\n        dataSet.Tables.Add(products);\n        dataSet.Tables.Add(suppliers);\n        dataSet.Tables.Add(features);\n\n        return dataSet;\n    }\n}