using Oracle.ManagedDataAccess.Client;\nusing System.Data;\n\npublic void FetchMultipleResults()\n{\n    string sql = @"\n        DECLARE\n            cur1 SYS_REFCURSOR;\n            cur2 SYS_REFCURSOR;\n            cur3 SYS_REFCURSOR;\n        BEGIN\n            OPEN cur1 FOR SELECT * FROM table1;\n            DBMS_SQL.RETURN_RESULT(cur1);\n            \n            OPEN cur2 FOR SELECT * FROM table2;\n            DBMS_SQL.RETURN_RESULT(cur2);\n            \n            OPEN cur3 FOR SELECT * FROM table3;\n            DBMS_SQL.RETURN_RESULT(cur3);\n        END;";\n\n    using (OracleConnection connection = new OracleConnection("your_connection_string"))\n    {\n        using (OracleCommand cmd = new OracleCommand(sql, connection))\n        {\n            OracleMultipleProperties mProps = new OracleMultipleProperties();\n            mProps.ArrayBindCount = 1;\n            cmd.Properties["OracleMultipleProperties"] = mProps;\n\n            connection.Open();\n            using (OracleDataReader reader = cmd.ExecuteReader())\n            {\n                DataTable dt1 = new DataTable();\n                dt1.Load(reader);\n                \n                if (reader.NextResult())\n                {\n                    DataTable dt2 = new DataTable();\n                    dt2.Load(reader);\n\n                    if (reader.NextResult())\n                    {\n                        DataTable dt3 = new DataTable();\n                        dt3.Load(reader);\n                    }\n                }\n            }\n        }\n    }\n}