// Assuming you have generated a typed DataSet called MyAppDataSet with tables and columns defined\nMyAppDataSet dataSet = new MyAppDataSet();\n\nusing (SqlCeConnection connection = new SqlCeConnection("YourConnectionString"))\n{\n    using (SqlCeCommand command = new SqlCeCommand("SELECT * FROM Users", connection))\n    {\n        using (SqlCeDataAdapter dataAdapter = new SqlCeDataAdapter(command))\n        {\n            // Fill the dataset\n            dataAdapter.Fill(dataSet.Users);\n        }\n    }\n}\n\n// Bind to a WPF control\nmyDataGrid.ItemsSource = dataSet.Users.DefaultView;